* {
  user-select: none;
}

.selectable {
  user-select: auto;
}

section {
  width: 80%;
  max-width: 1200px;
  margin: 0 auto;
}

input {
  display: none;
}

.test {
  padding: 20px;
  background-color: #eee;
}
.test:hover {
  background-color: #28dd2a;
}

.sortController {
  display: flex;
  margin: 0 auto;
}
.sortController .checker {
  margin: 10px;
}
.sortController .checker label {
  transition: 150ms;
  position: relative;
  padding: 10px;
  color: #333;
  cursor: pointer;
}
.sortController .checker label::before {
  content: "- ";
  color: #fff;
}
.sortController .checker label:hover {
  color: orange;
}
.sortController .checker input:checked + label {
  color: orange;
}
.sortController .checker input:checked + label::before {
  content: "- ";
  color: orange;
}

.itemList {
  display: flex;
  flex-wrap: wrap;
}
.itemList .item {
  margin: 10px;
}
.itemList .cd {
  display: block;
  transition: 250ms;
}
