#sortController {
  display: flex;
  margin: 0 auto;
}
#sortController .checker {
  margin: 10px;
}
#sortController .checker label {
  transition: 350ms;
  position: relative;
  padding: 10px;
  color: #333;
  cursor: pointer;
}
#sortController .checker label::before {
  position: absolute;
  left: -5px;
  transition: 300ms;
  content: "- ";
  color: #fff;
}
#sortController .checker label:hover {
  color: orange;
}
#sortController .checker input:checked + label {
  font-weight: bold;
  color: orange;
}
#sortController .checker input:checked + label::before {
  content: "- ";
  font-weight: bold;
  color: orange;
}

