body {
  margin: 0;
  padding: 0;
}

h2 {
  font-size: 18px;
}

header {
  text-align: center;
  width: 80%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 5px;
}
header h1 {
  font-size: 14px;
  color: #fff;
}
header img {
  width: 50px;
