@media screen and (max-width: 767px) {
  header img {
    width: 30px;
    height: 30px;
  }
}

section {
  width: 80%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 5px;
}

input {
  display: none;
}

#sortController {
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  width: 80%;
  margin: 0 auto;
}
@media screen and (max-width: 767px) {
  #sortController {
    width: 100%;
    position: sticky;
    top: 0;
    background-color: rgba(255, 255, 255, 0.8);
  }
}
#sortController .checker {
  text-align: center;
  margin: 10px;
  width: 100px;
}
#sortController .checker label {
  transition: 350ms;
  position: relative;
  padding: 10px;
  color: #333;
  cursor: pointer;
}
#sortController .checker label::before {
  visibility: hidden;
  position: absolute;
  left: -5px;
  transition: 300ms;
  color: #fff;
  content: "- ";
}
@media screen and (min-width: 768px) {
  #sortController .checker label:hover {
    color: orange;
  }
}
#sortController .checker input:checked + label {
  color: orange;
}
#sortController .checker input:checked + label::before {
  visibility: visible;
  content: "- ";
  font-weight: bold;
  color: orange;
}

.about {
  background-color: #ec6711;
  transition: 250ms;
  visibility: visible;
  display: block;
  height: 250px;
  color: #FFF;
}
@media screen and (max-width: 767px) {
  .about {
    height: 420px;
  }
}
.about .content {
  padding: 40px 0;
  display: block;
  width: 80%;
  margin: 0 auto;
}
.about .content .name {
  display: flex;
  padding: 10px 0;
}
.about .content .name .ja {
  margin-left: 0px;
}
.about .content .name .en {
  margin-left: 10px;
}
.about .content p {
  display: block;
  word-wrap: break-word;
  text-align: justify;
  font-size: 12px;
  line-height: 2em;
}

.itemList {
  display: flex;
  flex-wrap: wrap;
  align-content: flex-start;
  min-height: 100vh;
}
.itemList .item {
  opacity: 1;
  max-width: 160px;
  max-height: 160px;
  width: 34vw;
  height: 34vw;
  margin: 10px;
}
.itemList .item:hover {
  transition: 100ms;
  box-shadow: 5px 5px orange;
}
.itemList .item img {
  width: 100%;
  height: 100%;
}
.itemList .hello {
  transition: 250ms;
  display: block;
}
.itemList .poster {
  transition: 250ms;
